def validate_field_with_jsonschema(value, col_name,schema):  #di=None,value=None,col_name=None):           # Function to validate a single field
    try:
        pattern_1 = r"[òóôõöøōőÒÓÔÕÖØŌŐοσоӧɵʘȯäëïüÿÄËÏÜŸ@!-#$^&*'\\]"
        pattern_2 = r"\b\w+\s{2,}\w+\b"

        if pd.isnull(value) or str(value).strip() == "":     #NA, N/A detection
            return "empty cell"

        if schema["properties"][col_name]["type"] == "string":

            if re.search(pattern_1, value):
                return  f"found invalid later in {value} "

            if re.search(pattern_2, value):
                return f"space occure in {value}"

        if schema["properties"][col_name]["type"] == "number":

            if re.search(pattern_2, value):
                return f"space occure in {value}"

        json_obj = {col_name: value}                 # Create a temporary JSON object with just the single field
        validate(instance=json_obj, schema=schema)   #field_schema)
        return None                                  # No errors

    except ValidationError as e:
        return str(e)  # Return the validation error message


def schemas(column_name):
    print("SCHEMA :")
    print("type ,formate, maxlenght, minlenght, pattern, enum, minimum, maximum ")
    print(f"enter schema for {col_name}")

    schema_details={}
    col_type = input(f"{type}")

    if col_type == "string":
        attributes = ['type','pattern', 'format', 'maxLength', 'minLength', 'enum', 'description']

    elif col_type == "number":
        attributes = ['type','minimum', 'maximum', "pattern"]


    for attr in attributes:
        if attr == "type":
            schema_details[attr]= col_type
            continue

        attr_value = input(f"{attr} :").strip()

        if attr_value:  # Add only non-empty values
            if attr == 'enum':
                try:
                    attr_value = json.loads(attr_value)  # Convert enum input to a list
                    if not isinstance(attr_value, list):
                        raise ValueError("Enum must be a list.")
                except json.JSONDecodeError:
                    print("Invalid enum format. Enter a valid JSON array (e.g., [\"USD\", \"EUR\"]).")
                    continue
            elif attr in ['minimum', 'maximum', 'maxLength', 'minLength']:
                attr_value = int(attr_value)  # Convert numeric inputs to integers

            schema_details[attr] = attr_value

    schema = {
        "$schema": "http://json-schema.org/draft-07/schema#",
        "type":  "object",
        "properties": { column_name: schema_details },
        "required": [column_name]  # Make the field required
      }

    di[column_name] = schema           # Save schema in the global dictionary
    return schema

